usersApp.filter('startFrom', function() {
    return function(input, start) {
        start = +start;
        if (input!==undefined) {
            return input.slice(start);
        }
    }
});
usersApp.filter('reverse', function() {
    return function(items) {
        if (items!==undefined) {
            return items.slice().reverse();
        }
    };
});