usersApp.factory('refreshUsers', [
    '$resource',
    function($resource) {
        return $resource('/users');
    }
])
usersApp.factory('deleteUser', [
    '$resource',
    function($resource) {
        return $resource('/users/delete/:userId', { userId: '@id' });
    }
])
usersApp.factory('addUser', [
    '$resource',
    function($resource) {
        return $resource('/users/add');
    }
])
usersApp.factory('updateUser', [
    '$resource',
    function($resource) {
        return $resource('/users/update/:userId', { userId: '@id' });
    }
])
usersApp.factory('getUser', [
    '$resource',
    function($resource) {
        return $resource('/users/:userId', { userId: '@id' });
    }
])