var module = angular.module("usersApp");

module.service('userRemoteService', UserRemoteService);

UserRemoteService.$inject = ['$resource'];
function UserRemoteService($resource) {
    var self = this;

	self.getAll = $resource('/users');
	self.auth = $resource('/login');
    self.delete = $resource('/users/delete/:userId', { userId: '@id' });
    self.create = $resource('/users/add');
    self.update = $resource('/users/update/:userId', { userId: '@id' });
    self.getById = $resource('/users/:userId', { userId: '@id' });
}